--
-- Create procedure `Proc_Exercise_Filter`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Exercise_Filter (IN state int, IN subjectID char(36), IN gradeID char(36), IN search varchar(255), IN pageSize int, IN pageIndex int)
COMMENT 'tim kiem va loc theo ten, mon, khoi, trang thai'
BEGIN
  DECLARE offsetValue int DEFAULT 0;
  IF pageIndex > 1 THEN
    SET offsetValue = (pageIndex - 1) * pageSize;
  END IF;
  SELECT
    e.ExerciseID,
    e.ExerciseName,
    e.ExerciseImage,
    e.ExerciseState,
    e.SubjectID,
    e.GradeID,
    e.CreatedDate,
    e.CreatedBy,
    e.ModifiedDate,
    e.ModifiedBy,
    g.GradeName,
    s.SubjectName,
    s.SubjectImage,
    COUNT(q.QuestionID) AS NumQuestion
  FROM exercise e
    JOIN grade g
      ON e.GradeID = g.GradeID
    JOIN subject s
      ON e.SubjectID = s.SubjectID
    LEFT JOIN question q
      ON e.ExerciseID = q.ExerciseID
  WHERE (e.ExerciseState = state
  OR state < 1)
  AND (e.SubjectID = subjectID
  OR subjectID IS NULL)
  AND (e.GradeID = gradeID
  OR gradeID IS NULL)
  AND (LOWER(e.ExerciseName) LIKE CONCAT('%', LOWER(search), '%')
  OR search IS NULL)
  GROUP BY e.ExerciseID
  ORDER BY e.CreatedDate DESC
  LIMIT pageSize OFFSET offsetValue;
END
$$

