--
-- Create procedure `Proc_Exercise_Paging`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Exercise_Paging (IN $Skip int, IN $Take int, IN $Sort varchar(100), IN $Where varchar(1000))
COMMENT 'Phân trang và tìm kiếm bài tập'
BEGIN
  -- Tao bang tam 'tmp_exercise' de lay thong tin bai tap
  -- Nen su dung bang tam vi bang nay co the phuc vu cho muc dich loc, sap xep, query theo dieu kien...
  -- Ngoai ra phai lay du lieu theo phan trang va tong so ban ghi nen su dung bang tam giup viec thuc hien de dang hon
  CREATE TEMPORARY TABLE IF NOT EXISTS `tmp_exercise` (
    ExerciseID char(36),
    ExerciseName varchar(255),
    GradeID char(36),
    GradeName varchar(255),
    SubjectID char(36),
    SubjectName varchar(255),
    ExerciseImage varchar(255),
    ExerciseStatus smallint,
    CountQuestion int,
    CreatedDate datetime,
    CreatedBy varchar(255),
    ModifiedDate datetime,
    ModifiedBy varchar(255)
  );

  -- Insert du lieu vao bang tam 
  -- Join vao 2 bang positions va department de lay thong tin
  INSERT INTO `tmp_exercise`
    SELECT
      e.ExerciseID,
      e.ExerciseName,
      e.GradeID,
      g.GradeName AS GradeName,
      e.SubjectID,
      s.SubjectName AS SubjectName,
      e.ExerciseImage,
      e.ExerciseStatus,
      (SELECT
          COUNT(*)
        FROM question q
        WHERE q.ExerciseID = e.ExerciseID) AS CountQuestion,
      e.CreatedDate,
      e.CreatedBy,
      e.ModifiedDate,
      e.ModifiedBy
    FROM `exercise` e
      LEFT JOIN `subject` s
        ON e.SubjectID = s.SubjectID
      LEFT JOIN `grade` g
        ON e.GradeID = g.GradeID;

  #Phan trang
  -- Neu nhu khong co dieu kien dau vao thi gan dieu kien 1=1 (dk luon dung - 1 cach de set dieu kien query mac dinh )
  IF IFNULL($Where, '') = '' THEN
    SET $Where = 'WHERE 1=1';
  END IF;

  IF IFNULL($Sort, '') = '' THEN
    SET $Sort = 'CreatedDate DESC'; -- Hien tai dang de defaut xet theo thu tu ModifiedDate giam dan (co the thay doi theo de bai)
  END IF;
  IF $Take = -1 THEN
    SET @filterQuery = CONCAT('SELECT * FROM `tmp_exercise` ', $Where, ' ORDER BY ', $Sort); -- Mac dinh truyen -1 (neu truyen -1 se lay toan bo ban ghi)
  ELSE
    SET @filterQuery = CONCAT('SELECT * FROM `tmp_exercise` ', $Where, ' ORDER BY ', $Sort, ' LIMIT ', $Skip, ',', $Take); -- Neu lay du lieu tren 1 page co truyen skip, take
  END IF;

  -- Lay thong tin cua nhan vien theo phan trang + toan bo ban ghi
  -- Su dung phuong phap execute de tra ve 2 thong tin trong 1 store
  PREPARE stmt1 FROM @filterQuery;
  EXECUTE stmt1;
  DEALLOCATE PREPARE stmt1;

  SET @filterQuery = CONCAT('SELECT count(ExerciseID) AS TotalCount FROM `tmp_exercise` ', $Where);
  PREPARE stmt1 FROM @filterQuery;
  EXECUTE stmt1;
  DEALLOCATE PREPARE stmt1;

  -- Drop temp table sau khi dung de giai phong du lieu
  DROP TEMPORARY TABLE `tmp_exercise`;
END
$$

