--
-- Create procedure `Proc_ExerciseTopic_InsertMultiple`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_ExerciseTopic_InsertMultiple (IN $ExerciseID char(36), IN topicIDs varchar(1000))
COMMENT 'Thêm nhiều bản ghi chủ đề mới'
BEGIN
  DECLARE topicID varchar(36);

  -- Thực hiện xóa tất cả bản ghi có id bài tập = exerciseID
  DELETE
    FROM exercisetopic
  WHERE $ExerciseID = ExerciseID;

  -- Thực hiện insert lại các bản ghi trong topicIDs
  WHILE topicIDs <> '' DO
    SET topicID = SUBSTRING_INDEX (topicIDs, ',', 1);
    INSERT INTO exercisetopic (ExerciseTopicID, ExerciseID, CreatedDate, CreatedBy, ModifiedDate, ModifiedBy)
      VALUES (topicID, $ExerciseID, NOW(), 'VMHieu', NOW(), 'VMHieu');
    SET topicIDs = SUBSTRING(topicIDs, LENGTH(topicID) + 2);
  END WHILE;
END
$$

