--
-- Create procedure `Proc_QuestionAnswer_GetByID`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_QuestionAnswer_GetByID (IN QuestionID char(36))
COMMENT 'Lấy bản ghi câu hỏi và đáp án của câu hỏi đó'
BEGIN
  SET @question = CONCAT('SELECT * FROM Question q WHERE q.QuestionID = "', QuestionID, '"');
  SET @answer = CONCAT('SELECT * FROM Answer a WHERE a.QuestionID = "', QuestionID, '"');

  PREPARE stmt1 FROM @question;
  EXECUTE stmt1;
  DEALLOCATE PREPARE stmt1;

  PREPARE stmt2 FROM @answer;
  EXECUTE stmt2;
  DEALLOCATE PREPARE stmt2;
END
$$

--
-- Create procedure `Proc_Exercise_GetAllByID`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Exercise_GetAllByID (IN ExerciseID char(36))
COMMENT 'Lấy thông tin của 1 bản ghi bài tập và các bản ghi câu hỏi, đáp án phụ thuộc vào bài tập này'
BEGIN
  -- Chuẩn bị câu lệnh đầu vào
  SET @exercise = CONCAT('SELECT * FROM exercise e WHERE e.ExerciseID = "', ExerciseID, '"');
  SET @question = CONCAT('SELECT * FROM question q WHERE q.ExerciseID = "', ExerciseID, '" ORDER BY CreatedDate ASC');
  SET @answer = CONCAT('SELECT a.* FROM answer a INNER JOIN question q ON a.QuestionID = q.QuestionID WHERE q.ExerciseID = "', ExerciseID, '" ORDER BY SortOder ASC');
  -- Gán câu lệnh đầu vào cho 1 biến
  PREPARE stmt1 FROM @exercise;
  PREPARE stmt2 FROM @question;
  PREPARE stmt3 FROM @answer;
  -- Thực thi câu lệnh
  EXECUTE stmt1;
  EXECUTE stmt2;
  EXECUTE stmt3;
  -- Giải phóng biến gán câu lệnh
  DEALLOCATE PREPARE stmt1;
  DEALLOCATE PREPARE stmt2;
  DEALLOCATE PREPARE stmt3;
END
$$

--
-- Create procedure `Proc_Answer_DeleteMultiple`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Answer_DeleteMultiple (IN $AnswerID text)
COMMENT 'Xóa nhiều bản ghi đáp án'
BEGIN
  SET @stmt = CONCAT('DELETE FROM answer WHERE AnswerID IN ', '("', $AnswerID, '")');
  -- Thực hiện câu lệnh
  PREPARE stmt FROM @stmt;
  EXECUTE stmt;
  DEALLOCATE PREPARE stmt;
END
$$

